import Navbar from "./Navbar";
import MetinBox from "./MetinBox";
import axios from "axios";
import React from "react";

import { useState } from 'react';
function App() {

      const [metin1, setMetin1] = useState("");  
      const [metin2, setMetin2] = useState("");  
      const [sonuc, setSonuc] = useState("");  

      const [length, setlength] = useState(3);  


      const [state, setState] = useState({ 0: "", 1: "" });
      const handleChange = e => {
          const { name, value } = e.target;
          setState(prevState => ({
              ...prevState,
              [name]: value
          }));
      };
      console.log(state)
      console.log(post)

      const [post, setPost] = React.useState(null)
      const baseURL = "https://jsonplaceholder.typicode.com/posts/1";
const fetchData = () => {
  axios.get(baseURL).then((response) => {
    setPost(response.data);
    })
}

      return (    
  <>

    <div style={{width:'100%',minHeight:'100vh',backgroundColor:'rgba(8,8,8,0.9)',color:'white',margin:0,padding:0}}>
  <Navbar/>
  <div class="container mt-3">
      <div class="row g-4">
        <div class="col-sm-12 col-md-6 col-lg-6">

          {
          Object.keys(state).map((item,index)=>{
            return(<MetinBox handleChange={handleChange}   name={index}/>
);

          }


          )
          }
      <div  class=" d-flex justify-content-around gap-2 py-2">

<div class="btn" onClick={(e) => {setState(prevState=>({...prevState,[Object.keys(state).length]: ""}))}}  role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Yeni Metin Ekle</div>

</div>


        </div>


        <div class="col-sm-12 col-md-12 col-lg-6 ">

        <form>
  <div class="form-group">
    <label for="exampleFormControlTextareaBirlestirilmis" style={{fontSize:'18px'}}>Birleştirilmiş Metin</label>
    <textarea readOnly class="form-control disable" onChange={(e) => setSonuc(e.target.value)} value={sonuc} style={{width:'100%',height:'250px',backgroundColor:'transparent',color:'white',borderRadius:0}} id="exampleFormControlTextareaBirlestirilmis" rows="3" value="sa"></textarea>
  </div>
</form>
<div  class=" d-flex justify-content-around gap-2 py-2">
  <div  class="btn   " onClick={(e) => {setSonuc(metin1+metin2)}} role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Metinleri Birleştir</div>

<div  class="btn   " role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Birleştirlimiş Metni Kaydet</div>

</div>

        </div>
      </div>
    </div>
{metin1 }
<br/>
{metin2 }
<br/>

{sonuc }

    </div>
    </>
  );
}

export default App;
