import React from 'react';
import { useState ,useEffect} from 'react';



function MetinBox({handleChange,name}) {



return (    
    <div class="col-sm-12 col-md-6 col-lg-12">
    <form>
<div class="form-group" >
<label for={`exampleFormControlTextarea${(name+1)}`} style={{fontSize:'18px'}}>{name+1}. Metin</label>
<textarea class="form-control" name={name}  onChange={(e) => handleChange(e)} style={{width:'100%',height:'250px',backgroundColor:'transparent',color:'white',borderRadius:0}} id={`exampleFormControlTextarea${(name+1)}`} rows="3"></textarea>
</div>
</form>

    </div>

);
}
export default MetinBox;
