import Navbar from "./Navbar";
import MetinBox from "./MetinBox";
import axios from "axios";
import React from "react";
import combineStrings from './axios.js';

import { useState } from 'react';
function App() {

      const [metin1, setMetin1] = useState("");  
      const [metin2, setMetin2] = useState("");  
      const [combinedText, setCombinedText] = useState("");  

      const [length, setlength] = useState(3);  


      const [state, setState] = useState({ 0: "", 1: "" });
      const handleChange = e => {
          const { name, value } = e.target;
          setState(prevState => ({
              ...prevState,
              [name]: value
          }));
      };
      console.log(state)

      const baseURL = "http://localhost:8080/api/v1/movies/finalText";
      const stringsArray = {
        "string1": "Hello, ",
        "string2": "world!"
    };
    const [strings, setStrings] = useState(['hello', 'world']);

    function handleCombine() {
      combineStrings(strings)
        .then(result => setResult(result));
    }




      return (    
  <>

    <div style={{width:'100%',minHeight:'100vh',backgroundColor:'rgba(8,8,8,0.9)',color:'white',margin:0,padding:0}}>
  <Navbar/>
  <div class="container mt-3">
      <div class="row g-4">
        <div class="col-sm-12 col-md-6 col-lg-6">

          {
          Object.keys(state).map((item,index)=>{
            return(<MetinBox handleChange={handleChange}   name={index}/>
);

          }


          )
          }
      <div  class=" d-flex justify-content-around gap-2 py-2">

<div class="btn" onClick={(e) => {setState(prevState=>({...prevState,[Object.keys(state).length]: ""}))}}  role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Yeni Metin Ekle</div>

</div>


        </div>


        <div class="col-sm-12 col-md-12 col-lg-6 ">

        <form>
  <div class="form-group">
    <label for="exampleFormControlTextareaBirlestirilmis" style={{fontSize:'18px'}}>Birleştirilmiş Metin</label>
    <textarea readOnly class="form-control disable"  value={combinedText} style={{width:'100%',height:'250px',backgroundColor:'transparent',color:'white',borderRadius:0}} id="exampleFormControlTextareaBirlestirilmis" rows="3"></textarea>
  </div>
</form>
<div  class=" d-flex justify-content-around gap-2 py-2">
  <div  class="btn   " onClick={handleCombine} role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Metinleri Birleştir</div>

<div  class="btn   " role="button" aria-disabled="true"  style={{backgroundColor:'#FF0303',color:'white',borderRadius:6,width:'100%'}}>Birleştirlimiş Metni Kaydet</div>

</div>

        </div>
      </div>
    </div>

    </div>
    </>
  );
}

export default App;
