import React from 'react';


function MetinBox(props) {

return (    
    <div class="col-sm-12 col-md-6 col-lg-12">
    <form>
<div class="form-group" >
<label for={`exampleFormControlTextarea${props.name+1}`} style={{fontSize:'18px'}}>{props.name+1}. Metin</label>
<textarea class="form-control" name={props.name}  onChange={(e) => props.handleChange(e)} style={{width:'100%',height:'250px',backgroundColor:'transparent',color:'white',borderRadius:0}} id="exampleFormControlTextarea1" rows="3"></textarea>
</div>
</form>
    </div>

);
}
export default MetinBox;
