import Navbar from "./Navbar";
import MetinBox from "./MetinBox";
import axios from "axios";
import React from "react";
import combineStrings from './combineStrings';
import './styles/index.css'
import { useState ,useEffect} from 'react';

import Slide from 'react-reveal/Slide';

function App() {


      const [combinedText, setCombinedText] = useState("");  
      const [timer, setTimer] = useState("");  

      const [strings, setStrings] = useState([]);
      const [changeControl, setchangeControl] = useState(false);


      const [state, setState] = useState({0:"",1:""});
      const handleChange = e => {
          const { name, value } = e.target;
          setState(prevState => ({
              ...prevState,
              [name]: value
          })


          );    
           setStrings(Object.values(state))
      };


    function handleCombine() {


    if(isEmpty()){
      alert("Lütfen Bütün Kutuları Doldurun")

    }
    else{
      setStrings(Object.values(state))

            combineStrings(strings)
        .then((result) => 
          {
            setCombinedText(result[0]) 
            setTimer(result[1])
          }
          );
    }



    }



    const deleteText = () => {
      setState(Object.values(state).filter((item,key)=>key !==Object.keys(state).length-1)) 

     }

     const isEmpty= () => {

      var isEmpty = false;
       Object.values(state).filter((item) => {
      if(item === ""){
        isEmpty = true
      }

    });

    return isEmpty;

     }

     const saveFun= () => {

       if(isEmpty() && changeControl){
        alert("Lütfen Bütün Kutuları Doldurun")
      }
       else{
        alert("hepsi kaydet")

       }
     }

useEffect(() => {
  setchangeControl(false)

  setStrings(Object.values(state))

}, [state])



      return (    
  <>

    <div style={{width:'100%',minHeight:'100vh',backgroundColor:'rgba(8,8,8,0.9)',color:'white',margin:0,padding:0}}>
  <Navbar/>
  <div class="container mt-3">
      <div class="row g-4">
        <div class="col-sm-12 col-md-12 col-lg-6">


          {
          Object.keys(state).map((item,index)=>{

            return(<MetinBox handleChange={handleChange}   name={index} />);

          }


          )
          }

      <div  class=" d-flex justify-content-around gap-2 py-2">

<div class="btn button" onClick={(e) => {setState(prevState=>({...prevState,[Object.keys(state).length]: ""}))}}  role="button" aria-disabled="true" >Yeni Metin Ekle</div>
{
  Object.keys(state).length > 2 ? (<div class={`btn delete-button`} onClick={e => deleteText()}  role="button" aria-disabled="true"  >Metin Sil</div>  ) : (null)
}
</div>


        </div>


        <div class="col-sm-12 col-md-12 col-lg-6 ">
        <Slide top>

        <form>
  <div class="form-group">
    <div className="d-flex justify-content-between">
         <label for="exampleFormControlTextareaBirlestirilmis" style={{fontSize:'18px'}}>Birleştirilmiş Metin</label>
            {
              timer !== "" &&  <div className="d-flex justify-content-between gap-2 ">Hesaplama Süresi: {timer} </div>
            } 

    </div>

    <textarea readOnly class="form-control disable"  value={combinedText} style={{width:'100%',height:'250px',backgroundColor:'transparent',color:'white',borderRadius:0}} id="exampleFormControlTextareaBirlestirilmis" rows="3"></textarea>
  </div>
</form>
</Slide>

<div  class=" d-flex justify-content-around gap-2 py-2">
  <div  class="btn  button " onClick={handleCombine} role="button" aria-disabled="true"  >Metinleri Birleştir</div>

<div  class="btn   button" role="button" onClick={saveFun} aria-disabled="true" >Birleştirlimiş Metni Kaydet</div>

</div>
   </div>
      </div>
    </div>

    </div>
    </>
  );
}

export default App;
